var guess = require('../guess.js'),
	query;

console.log('Answer y/n\n');

guess(0, 255, function(num, _query) {

	query = _query;

	console.log('X > ' + num);

}, function(num) {

	query = null;

	console.log('Number is ' + num);

});

process.stdin.resume();
process.stdin.setEncoding('utf8');

process.stdin.on('data', function(text) {

	if (query)

		query(/y|s/i.test(text));

});